#include <mpi.h>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>

int main(int argc, char** argv) {
    int rank, size;
    double local_random, global_sum, global_avg;

    // Initialize MPI
    MPI_Init(&argc, &argv);
    MPI_Comm_rank(MPI_COMM_WORLD, &rank);
    MPI_Comm_size(MPI_COMM_WORLD, &size);

    // Seed the random number generator
    srand(time(NULL) + rank);

    // Generate a random number
    local_random = (double)rand() / RAND_MAX;

    // Print the random number generated by each process
    printf("Process %d generated random number: %f\n", rank, local_random);

    // Reduce to calculate the sum of all random numbers
    MPI_Reduce(&local_random, &global_sum, 1, MPI_DOUBLE, MPI_SUM, 0, MPI_COMM_WORLD);

    // Calculate the average on the root process
    if (rank == 0) {
        global_avg = global_sum / size;
        printf("Average of random numbers: %f\n", global_avg);
    }

    // Finalize MPI
    MPI_Finalize();
    return 0;
}